import React, { useEffect, useState } from 'react';
import './ManageTour.css';

const ManageTour = () => {
    const [tours, setTours] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5000/services')
            .then(res => res.json())
            .then(data => setTours(data))
    }, []);

    const handleDelete = id => {
        const url = `http://localhost:5000/services/${id}`;
        fetch(url, {
            method: 'DELETE'
        })
            .then(res => res.json())
            .then(data => {
                console.log(data);
                if (data.deletedCount) {
                    const remaining = tours.filter(tour => tour._id !== id);
                    alert('Your tour package is successfully deleted');
                    setTours(remaining);
                }

            })
    }
    return (
        <div className="" >
            <h2>My Creating Tour package</h2>
            {
                tours.map(tour =>
                    <div className="" key={tour._id}>
                        <div className="service-container">
                            <div>
                                <h5>{tour.name}</h5>
                            </div>
                            <div>
                                <h5>
                                    <button onClick={() => handleDelete(tour._id)} >Delete ❌</button > <button className="bg-success ms-2 text-white  " >Update ✔ </button>
                                </h5>
                            </div>
                        </div>



                    </div>)
            }
        </div >
    );
};

export default ManageTour;
