import React from 'react';
import { useForm } from "react-hook-form";
import './Booking.css';


const Booking = () => {
    const { register, handleSubmit } = useForm();
    const onSubmit = data => {
        console.log(data);

    }
    return (
        <div className="add-booking" >
            <h1>Booking Your Tour Schedule</h1>
            <form onSubmit={handleSubmit(onSubmit)}>
                <input {...register("name", { required: true, maxLength: 20 })} placeholder=" Package Name" />
                <textarea {...register("description")} placeholder=" Package Description" />
                <input type="number" {...register("price")} placeholder=" Total Cost" />
                <input {...register("img")} placeholder=" Image Url " />
                <input className="bg-warning" type="submit" />
            </form>
        </div>
    );
};

export default Booking;
