import React, { useEffect, useState } from 'react';
import "./Booking.css";
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
// import Appointments from '../../Appointments/Appointments';



const Booking = () => {

    const { serviceId } = useParams();
    // const [details, setDetails] = useState([]);
    const [service, setService] = useState({});


    // here we load the data
    useEffect(() => {
        fetch(`http://localhost:5000/services/${serviceId}`)
            .then(response => response.json())
            .then(data => setService(data))

    }, []);


    //after load all data it calls 

    // useEffect(() => {
    //     const foundService = details.find(
    //         service => service.login.id == serviceId);
    //     setService(foundService);
    // }, [details, serviceId])

    //---------------

    return (


        <div className="">
            <h2>{serviceId}</h2>
            <h3>Details of: {service.name}</h3>


            <h4 className="fw-bolder"> {service?.name} SERVICE IS BOOKED .  </h4>
            <img className="w-25 mt-3 rounded-3 mt-3" src={service?.img} alt="" />

            <Link to="/">
                <button className="btn btn-warning mt-2 fw-bold p-3 rounded-pill">   Go Back </button>
            </Link>



        </div>




    );
};

export default Booking;
