import axios from 'axios';
import React from 'react';
import { useForm } from "react-hook-form";
import './Booking.css';


const Booking = () => {
    const { register, handleSubmit, reset } = useForm();
    const onSubmit = data => {
        console.log(data);

        //post related code ---------->
        axios.post('http://localhost:5000/services', data)
            .then(response => {
                if (response.data.insertedId) {
                    alert('Your booking is succesfully added');
                    reset();
                }

            })
        //---------------------->
    }
    return (
        <div className="add-booking" >
            <h1>Booking and place order Your Tour Schedule</h1>
            <form onSubmit={handleSubmit(onSubmit)}>
                <input className="fw-bold" {...register("name", { required: true, maxLength: 20 })} placeholder=" Package Name" />
                <textarea className="fw-bold" {...register("description")} placeholder=" Package Description" />
                <input className="fw-bold" type="number" {...register("price")} placeholder=" Total Cost" />
                <input className="fw-bold" {...register("img")} placeholder=" Image Url " />
                <input className="bg-warning rounded-pill p-2 border-0 fw-bold fs-4" type="submit" />
            </form>
        </div>
    );
};

export default Booking;
