import React, { useEffect, useState } from 'react';

const ManageTour = () => {
    const [tours, setTours] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5000/services')
            .then(res => res.json())
            .then(data => setTours(data))
    }, [])
    return (
        <div>
            <h2>My Creating Tour package</h2>
            {
                tours.map(tour => <div key={tour._id}>

                    <h5>{tour.name} <button>Delete ❌</button> <button className="bg-success ms-2 text-white rounded-pill " >Update ✔ </button> </h5>


                </div>)
            }
        </div>
    );
};

export default ManageTour;
