import React, { useEffect, useState } from 'react';
import { Card } from 'react-bootstrap';
import './ManageTour.css';

const ManageTour = () => {
    const [tours, setTours] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5000/services')
            .then(res => res.json())
            .then(data => setTours(data))
    }, []);

    const handleDelete = id => {
        const url = `http://localhost:5000/services/${id}`;
        fetch(url, {
            method: 'DELETE'
        })
            .then(res => res.json())
            .then(data => {
                console.log(data);
                if (data.deletedCount) {
                    const remaining = tours.filter(tour => tour._id !== id);
                    alert('Your tour package is successfully deleted');
                    setTours(remaining);
                }

            })
    }
    return (
        <div className="" >
            <h2>My Creating Tour package</h2>
            {
                tours.map(tour =>
                    <div key={tour._id}>
                        <Card style={{ width: '18rem' }}>
                            <Card.Body>
                                <Card.Title>{tour.name}</Card.Title>
                                <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
                                <Card.Text>
                                    Some quick example text to build on the card title and make up the bulk of
                                    the card's content.
                                </Card.Text>
                                <Card.Link href="#">Card Link</Card.Link>
                                <Card.Link href="#">Another Link</Card.Link>
                            </Card.Body>
                        </Card>

                        {/* <h5> Booking Package: {tour.name}
                     <br />

                        <button onClick={() => handleDelete(tour._id)} >Delete ❌</button > <button className="bg-success ms-2 text-white  " >Update ✔ </button> </h5> */}
                    </div>)
            }
        </div >
    );
};

export default ManageTour;
