import React, { useEffect, useState } from 'react';
import { Card, Col, ListGroup, ListGroupItem, Row } from 'react-bootstrap';
import './ManageTour.css';

const ManageTour = () => {
    const [tours, setTours] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5000/services')
            .then(res => res.json())
            .then(data => setTours(data))
    }, []);

    const handleDelete = id => {
        const url = `http://localhost:5000/services/${id}`;
        fetch(url, {
            method: 'DELETE'
        })
            .then(res => res.json())
            .then(data => {
                console.log(data);
                if (data.deletedCount) {
                    const remaining = tours.filter(tour => tour._id !== id);
                    alert('Your tour package is successfully deleted');
                    setTours(remaining);
                }

            })
    }
    return (
        <div className="m-5 mb-5" >
            <h2>My Tour packag </h2>
            {
                tours.map(tour =>
                    <div className="container-fluide bg-warning" key={tour._id}>
                        <div className="">
                            <Row xs={1} md={2} className="g-4">
                                {Array.from({ length: 4 }).map((_, idx) => (
                                    <Col>
                                        <Card>

                                            <Card.Body>
                                                <Card.Title>Card title</Card.Title>
                                                <Card.Text>
                                                    This is a longer card with supporting text below as a natural
                                                    lead-in to additional content. This content is a little bit longer.
                                                </Card.Text>
                                                <Card.Footer>
                                                    <small className="text-muted">Last updated 3 mins ago</small>
                                                </Card.Footer>
                                            </Card.Body>
                                        </Card>
                                    </Col>
                                ))}
                            </Row>
                            {/* <div>
                                <h2> <small>Package Name: </small>{tour.name}</h2>
                            </div>
                            <div>
                                <button onClick={() => handleDelete(tour._id)} >Delete ❌</button >
                                <button className="bg-success ms-2 text-white  " >Update ✔ </button>
                            </div> */}
                        </div>
                    </div>)
            }
        </div >
    );
};

export default ManageTour;
