import React from 'react';

const Offer = ({ offer }) => {
    const { name, regular, discout, description, hotel, contact } = offer;
    return (
        <div className=" col-lg-4 col-sm-6 col-12 mt-5 mb-4 ">

            <h3 className="mt-4 fw-bolder fs-2">{name}</h3>
            <h5 className="text-danger ">{regular}</h5>
            <h6 className="text-danger ">{discout}</h6>
            <p>{description}</p>
            <p>{hotel}</p>
            <p>{contact}</p>
        </div>
    );
};
export default Offer;
