import React, { useEffect, useState } from 'react';
import "./Booking.css";
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
// import Appointments from '../../Appointments/Appointments';



const Booking = () => {

    const { serviceId } = useParams();
    const [details, setDetails] = useState([]);
    const [singleService, setSingleService] = useState({});


    // here we load the data
    useEffect(() => {
        fetch(`http://localhost:3000/booking/${serviceId}`)
            .then(response => response.json())
            .then(data => setDetails(data.service))

    }, []);


    //after load all data it calls 

    useEffect(() => {
        const foundService = details.find(
            service => service.login.id == serviceId);
        setSingleService(foundService);
    }, [details, serviceId])

    //---------------

    return (


        <div className="service  ">
            <h2>{serviceId}</h2>
            <h3>Details of: {serviceId.name}</h3>
            {/* <div className="service  "> */}
